mapper.select:
select * from(select zzz.*, rownum RNUM from( select to_char(wh_dt,'YYYY-MM-DD (DY)', 'NLS_DATE_LANGUAGE=KOREAN') as "wh_dt" ,wh_no||'' as "wh_no" ,order_inven_no||'' as "order_inven_no" ,(select emp_name from employee where emp_no = (select emp_no from book where isbn13 = (select isbn13 from books_inven_order_info where order_inven_no = w.order_inven_no))) as "emp_name" ,(select to_char(order_dt,'YYYY-MM-DD DY', 'NLS_DATE_LANGUAGE=KOREAN') from books_inven_order_info where order_inven_no = w.order_inven_no)||'' as "order_dt" ,(select order_stock_cnt from books_inven_order_info where order_inven_no = w.order_inven_no )||'' as "order_stock_cnt" ,(select cost from books_inven_order_info where order_inven_no = w.order_inven_no)||'' as "cost" ,(select supply_rate from books_inven_order_info where order_inven_no = w.order_inven_no )||'' as "supply_rate" ,(select to_char(store_req_dt,'YYYY-MM-DD DY', 'NLS_DATE_LANGUAGE=KOREAN') from books_inven_order_info where order_inven_no = w.order_inven_no)||'' as "store_req_dt" ,(select branch_name from code_branch where branch_cd = (select branch_cd from books_inven_order_info where order_inven_no = w.order_inven_no))||'' as "branch_name" ,(select isbn13 from books_inven_order_info where order_inven_no = w.order_inven_no) as "isbn" ,(select book_name from book where isbn13 = (select isbn13 from books_inven_order_info where order_inven_no = w.order_inven_no) ) as "book_name" ,(select publisher from book where isbn13 = (select isbn13 from books_inven_order_info where order_inven_no = w.order_inven_no) ) as "book_publisher" ,(select cat_name from CODE_CATEGORY where cat_cd = (select cat_cd from book where isbn13 = (select isbn13 from books_inven_order_info where order_inven_no = w.order_inven_no )) ) as "cat_name" from warehousing_info w where 1=1
order by ${sort}
order by wh_dt desc
) zzz ) where RNUM>=${selectPageNo*rowCntPerPage-rowCntPerPage+1} and RNUM<=${selectPageNo*rowCntPerPage}

mapper.update:
       	update warehousing_info 
       	set wh_dt = sysdate
		where wh_no = #{wh_no}
