mapper.select:
select * from(select zzz.*, rownum RNUM from( select b.isbn13 as "ISBN13" ,b.book_name as "BOOK_NAME" ,c1.cat_name as "CAT_NAME" ,b.book_price as "BOOK_PRICE" ,b.book_pages||'' as "BOOK_PAGES" ,b.writer as "WRITER" ,b.publisher as "PUBLISHER" ,b.size_cd||'' as "SIZE_CD" ,b.is_print as "IS_PRINT" ,b.emp_no||'' as "EMP_NO" ,to_char(b.published_dt,'YYYY-MM-DD') as "PUBLISHED_DT" ,b.editor as "EDITOR" ,s1.isbn_cnt as "ISBN_CNT" ,c2.branch_name as "BRANCH_NAME" ,b.reg_dt as "REG_DT" from book b inner join code_category c1 on b.cat_cd = c1.cat_cd inner join stock_info s1 on b.isbn13 = s1.isbn13 inner join code_branch c2 on s1.branch_cd = c2.branch_cd where 1=1
and b.emp_no = '${emp_no}'
order by ${sort}
order by b.reg_dt desc
) zzz ) where RNUM>=${selectPageNo*rowCntPerPage-rowCntPerPage+1} and RNUM<=${selectPageNo*rowCntPerPage}

select * from(select zzz.*, rownum RNUM from( select w.emp_no as "EMP_NO" , e.emp_name as "emp_name" , d.dep_name as "dep_name" , cj.jikup as "jikup" , w.days_attended as "days_attended" , w.days_absent as "days_absent" , w.days_leaving_early as "days_leaving_early" , w.days_outduty as "days_outduty" , w.days_late as "days_late" from work_days_mng w, employee e, dept d, code_jikup cj where w.emp_no=e.emp_no and e.dep_no=d.dep_no and e.jikup_cd=cj.jikup_cd
order by ${sort}
order by w.emp_no
) zzz ) where RNUM>=${selectPageNo*rowCntPerPage-rowCntPerPage+1} and RNUM<=${selectPageNo*rowCntPerPage}

mapper.insert:
insert into books_inven_order_info values(
	(select nvl(max(order_inven_no), 0)+1 from books_inven_order_info)
	,#{isbn}
	,#{isbn_cnt}
	,#{supply_rate}
	,(#{isbn_cnt}*(select book_price from book where isbn13=#{isbn}))
	,sysdate
	,01
	,to_date(#{datepicker}, 'YYYY-MM-DD')
)

insert into emp_dayoff_apply values(
	(select nvl(max(dayoff_apply_no), 0)+1 from emp_dayoff_apply)
	,#{emp_no}
	,to_date(to_char(sysdate, 'YYYY-MM-DD'), 'YYYY-MM-DD')
	,#{dayoff_cd}
	
	,#{harf_dayoff}
	
	
	,#{using_dayoff}
	
	,to_date(#{start_dayoff}, 'YYYY-MM-DD')
	,to_date(#{end_dayoff}, 'YYYY-MM-DD')
	,'N'
	)

mapper.update:
update emp_dayoff_info set
remain_dayoff=remain_dayoff-
	
	#{harf_dayoff}
	
	
	#{using_dayoff}
	 where emp_no=#{emp_no}
